mapper.insert:
INSERT INTO transaction(
	tran_no, prod_no, buyer_id, payment_option, receiver_name, receiver_phone, demailaddr, dlvy_request, tran_status_code, order_date, dlvy_date,total_price
) VALUES (
seq_transaction_tran_no.nextval, #{purchaseProd.prodNo}, #{buyer.userId}, #{paymentOption}, #{receiverName}, #{receiverPhone}, #{divyAddr}, #{divyRequest}, 'b', SYSDATE, #{divyDate},#{totalPrice}
)

	INSERT INTO transaction_detail(
	detail_no, tran_no, prod_no, quantity, type_price
) VALUES (
seq_transaction_detail_detail_no.nextval, #{tranNo}, #{purchaseProd.prodNo}, #{typeQuantity}, #{typePrice}
)

mapper.update:
UPDATE TRANSACTION
PAYMENT_OPTION=#{paymentOption}, RECEIVER_NAME=#{receiverName}, RECEIVER_PHONE=#{receiverPhone}, DEMAILADDR=#{divyAddr}, DLVY_REQUEST=#{divyRequest}, TRAN_STATUS_CODE=#{tranCode}, ORDER_DATE=SYSDATE, DLVY_DATE=#{divyDate}, TOTAL_PRICE=#{totalPrice}
WHERE TRAN_NO=#{tranNo}

UPDATE TRANSACTION
SET TRAN_STATUS_CODE=#{tranCode}
WHERE TRAN_NO=#{tranNo}
